import { useState } from "react";
import './App.css';
const Calculator = () => {

    const [result,setResult] = useState("")
    

    const handleClick = (e) => {
        setResult(result.concat(e.target.name))
    }
    const clear = () => {
        setResult("")
    }

    const backspace = (e) => {
        setResult(result.slice(0,-1))
    }

    const calculate = () => {
        if (result===""){
            setResult("enter valid input")
        }
        else{
            try{    
            setResult(eval(result).toString())
            } catch(error){
                setResult("error")
            }
        }
    }
  

    return ( 
        <>
        <h1>Anmole calci</h1>
      
        <div className="container">
            <form>
                <input type="text" value={result} />
            </form>
            <div className="keys">
                <button name="clear" onClick={clear} id="clear">AC</button>
                <button name="c" onClick={backspace} id="backspace">C</button>
                <button name="/" onClick={handleClick} id="divide">&divide;</button>
                <button name="7" onClick={handleClick}>7</button>
                <button name="8" onClick={handleClick}>8</button>
                <button name="9" onClick={handleClick}>9</button>
                <button name="*" onClick={handleClick} id="multiply">&times;</button>
                <button name="4" onClick={handleClick}>4</button>
                <button name="5" onClick={handleClick}>5</button>
                <button name="6" onClick={handleClick}>6</button>
                <button name="-" onClick={handleClick} id="sub">&ndash;</button>
                <button name="1" onClick={handleClick}>1</button>
                <button name="2" onClick={handleClick}>2</button>
                <button name="3" onClick={handleClick}>3</button>
                <button name="+" onClick={handleClick} id="add">+</button>
                <button name="0" onClick={handleClick}>0</button>
                <button name="." onClick={handleClick}>.</button>
                <button name="=" onClick={calculate} id="equal">=</button>
            </div>
        </div>
        </>
     );
}
 
export default Calculator;
